const SearchBox = (d) => {
  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({});

  <div className="search-box">
    <input
      className="search-bar"
      type="text"
      placeholder="Enter your City or County for weather info...üîç"
      onChange={(e) => setQuery(e.target.value)}
      value={query}
      onKeyPress={search}
    />
  </div>;

  {
    /* ‚¨áÔ∏è CHANGE NAME FROM warm TO night ‚¨áÔ∏è*/
  }
  {
    typeof weather.main != "undefined" ? (
      <div>
        <div className="Weather-Component">
          <div className="location-box">
            <div className="location">
              {weather.name}, {weather.sys.country}
            </div>
            <div className="date">{dateBuilder(new Date())}</div>
          </div>
          <div className="temp">
            {Math.round(weather.main.temp * 1.8) + 32}¬∞F
            <div className="weather">{weather.weather[0].main}</div>
          </div>
          <div className="weather">
            üå° Feels Like: {Math.round(weather.main.feels_like * 1.8 + 32)}¬∞F
          </div>
          <div className="weather">üåÇ Humidity: {weather.main.humidity}%</div>
          <div className="weather">
            üìâ Min Temp: {Math.round(weather.main.temp_min * 1.8 + 32)}
            ¬∞F
          </div>
          <div className="weather">
            üìà Max Temp: {Math.round(weather.main.temp_max * 1.8 + 32)}
            ¬∞F
          </div>
          <div className="weather">{weather.wind.speed}</div>
        </div>
      </div>
    ) : (
      " "
    );
  }
};

export default SearchBox;
