import React, { useState } from "react";
import "./App.css";

const weatherAPI = {
  key: "6bfb68468aecdf3f230fcdc9615a4160",
  base: "https://api.openweathermap.org/data/2.5/",
};

function App() {
  // Create separate component for this also
  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({});

  const search = (evt) => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then((res) => res.json())
        .then((result) => {
          setWeather(result);
          setQuery("");
          console.log(result);
        });
    }
  };

  // Create separate component for dateBuilder
  const dateBuilder = (d) => {
    let months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    let days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`;
  };

  return (
    <div className="App">
      <header></header>
      <main>
        <div className="search-box">
          <input className="search-bar" type="text" placeholder="Search...üîç" />
        </div>

        <div className="App-title">
          <h1>Weather App</h1>
        </div>

        {/* ‚¨áÔ∏è CHANGE NAME FROM warm TO night ‚¨áÔ∏è*/}
        <div className="Weather-Component">
          <div className="location-box">
            <div className="location">Ponte Vedra, FL</div>
            <div className="date">{dateBuilder(new Date())}</div>
          </div>
          <div className="temp">15¬∞C</div>
          <div className="weather">Sunny</div>
        </div>
      </main>
    </div>
  );
}

export default App;
